import React, { Component } from 'react';
import axios from 'axios'

class IPAddresses extends Component {
    state = {
        ip_addresses: null
    };

    componentDidMount() {
        this.fetchIPs();
        this.getLocationData();
    }

    fetchIPs = async () => {
        await axios
            .get("http://localhost:5000/fetch_ips")

            .then(response => {
                let all_ips = response.data
                console.log("all_ips = " + all_ips);

                let all_location_data = []
                all_ips.map(ip => {
                    axios
                        .get("http://localhost:5000/fetch_data_by_ip/" + ip)
                        .then(response => {
                            console.log(JSON.stringify(response.data))
                            let all_location_data = response.data;
                            //let first_ip = all_location_data[0].ip;
                            //let first_lat = all_location_data[0].latitude;
                            //let first_long = all_location_data[0].longitude;

                            //console.log(first_ip + " at lat " + first_lat + ", long " + first_long);
                        })
                        .catch()
                })





                this.setState({
                    ip_addresses: all_ips
                })
            });
    }

    getLocationData = async () => {
        await axios
            .get("http://localhost:5000/fetch_data_by_ip")
            .then(response => {
                console.log(JSON.stringify(response.data))
                let all_location_data = response.data;
                //let first_ip = all_location_data[0].ip;
                //let first_lat = all_location_data[0].latitude;
                //let first_long = all_location_data[0].longitude;

                //console.log(first_ip + " at lat " + first_lat + ", long " + first_long);
            })
            .catch()

    }

    render() {
        return (
            <div>
                {this.state.ip_addresses ? <p>First IP: {this.state.ip_addresses[0]}</p> : <div/>}
            </div>
        );
    }
}

export default IPAddresses;


